(10664, 'BLOCK_A2KMCJBKO3', 'text', NULL, NULL, '[{"optionType":"colors","priority":80},{"optionType":"effects","priority":90,"_id":"c10196"},{"optionType":"advancedCSS","priority":200,"cssClasses":"lp-attaque"}]', '<p style="text-align:center">L’Ecole Paul et Virginie is located in Tamarin, on the southwest coast of Mauritius in the "Smart and Happy Village Cap Tamarin."</p>\n', NULL, '[]', 7329),
(10665, 'BLOCK_Q82RIDDYZL', 'text', NULL, NULL, '[{"optionType":"colors","priority":80},{"optionType":"effects","priority":90,"_id":"c10214"},{"optionType":"advancedCSS","priority":200,"cssClasses":"paragraph"}]', '<p><strong>L’Ecole Paul et Virginie </strong>operates on its own funds from school fees, ensures that the working environment of students and staff is an exceptional place to live, secure, green and well maintained, facilitating learning and development for everyone.</p>\n', NULL, '[]', 7330),
