INSERT INTO `ID302_block` (`id`, `ref`, `type`, `subType`, `index`, `options`, `content`, `moduleName`, `moduleOptions`, `parentColumn`) VALUES
(11664, 'BLOCK_KFCSIKV481', 'text', NULL, NULL, '[{"optionType":"colors","priority":80},{"optionType":"effects","priority":90,"_id":"c3516"},{"optionType":"advancedCSS","priority":200,"cssClasses":"paragraph"}]', '<p><strong>L''école Paul et Virginie</strong>, qui fonctionne sur ses fonds propres alimentés uniquement par les frais de scolarité et de dons, veille à ce que le cadre de travail des élèves et de ses personnels soit un lieu de vie exceptionnel, sécurisé, verdoyant et bien entretenu facilant les apprentissages et le développement de chacun.</p>\n', NULL, '[]', 8150),
(11665, 'BLOCK_RR88TXPDZH', 'image', NULL, NULL, '[{"optionType":"image","priority":70,"contextInfos":[],"file":{"id":47,"title":[],"desc":{"fr_FR":"\\u00c9cole \\u00e9l\\u00e9mentaire Tamarin","en_FR":"Elementary school Tamarin"},"fileUrl":"\\/ressources\\/images\\/b21651074620.jpg","mimeType":"image\\/jpeg","ext":"jpg","thumb":"\\/ressources\\/images\\/thumb\\/thumb_b21651074620.jpg","name":"b21651074620.jpg","createdAt":"2020-06-16T11:34:00+0000","modified_at":"2020-06-25T05:20:00+0000","originalName":"e11d9edb1bec","useCount":null,"focus":{"x":null,"y":null}},"link":{"target":"_self","name":[],"anchor":"","type":4,"showTitle":true,"showDesc":true,"href":47}},{"optionType":"effects","priority":90,"_id":"c3529"},{"optionType":"advancedCSS","priority":200,"cssClasses":""}]', NULL, NULL, '[]', 8151),
(11666, 'BLOCK_Y8IY0Y6CEV', 'text', NULL, NULL, '[{"optionType":"colors","priority":80},{"optionType":"effects","priority":90,"_id":"c3544"},{"optionType":"advancedCSS","priority":200,"cssClasses":""}]', '<h2>Des espaces de classe favorisant les apprentissages:</h2>\n\n<p>Les batiments sont répartis sur deux hectares de terrain :&nbsp;</p>\n\n<p>- 6 salles de maternelle en 3 petits "blocs"</p>\n\n<p>- 3 salles de CP et 3 salles de CE2 dans un batiment</p>\n\n<p>- 3 salles de CE1, 2 salles de CM1 et une salle pour le dispositif d''aide pour les Elèves à Besoins Educatifs Particuliers dans un batiment</p>\n\n<p>- 5 salles d''anglais, deux salles de CM2 et une salle pour le dispositif pour les Elèves Autrement Capables dans un bâtiment.</p>\n\n<p>&nbsp;</p>\n', NULL, '[]', 8152),
(11667, 'BLOCK_LTMSJE7BTJ', 'text', NULL, NULL, '[{"optionType":"colors","priority":80},{"optionType":"effects","priority":90,"_id":"c3550"},{"optionType":"advancedCSS","priority":200,"cssClasses":""}]', '<blockquote>\n<p>L’Ecole Paul et Virginie possède&nbsp;de multiples atouts&nbsp;qui en font un établissement d’excellence.</p>\n</blockquote>\n', NULL, '[]', 8152),
(11668, 'BLOCK_NFMOAWR2BC', 'text', NULL, NULL, '[{"optionType":"colors","priority":80},{"optionType":"effects","priority":90,"_id":"c3561","boxShadow":"5px 5px 5px 2.5px rgba(0,0,0,0.4)"},{"optionType":"advancedCSS","priority":200,"cssClasses":"text-css1"}]', '<h4><strong><span style="color:#e35c22">La communication quotidienne:</span></strong></h4>\n\n<p>- à l''accueil, le responsable de vie scolaire ou/et la cheffe d''établissement répondront à vos questionnements ou vous redirigeront vers l''interlocuteur adapté.</p>\n\n<p>- à la réception, vous pourrez avoir des réponses administratives.</p>\n\n<p>- par téléphone, vous pourrez joindre les interlocuteurs de l''équipe administrative.</p>\n\n<p>- via la page Facebook de l''école, vous pouvez avoir les dernières informations.</p>\n', NULL, '[]', 8153),
(11669, 'BLOCK_J9YR7VG11I', 'text', NULL, NULL, '[{"optionType":"colors","priority":80},{"optionType":"effects","priority":90,"_id":"c3567"},{"optionType":"advancedCSS","priority":200,"cssClasses":"lp-avantages"}]', '<ul>\n	<li>Le compte-rendu du CCL du 25&nbsp;juin 2020 est disponible <a data-link-type="internal" href="/ressources/fichiers/ded8930b6c25.pdf">ici</a></li>\n	<li>Le compte-rendu du CCL du 5 décembre 2020&nbsp;est disponible&nbsp;<a data-link-type="internal" download="52b200868d44.pdf" href="/ressources/fichiers/52b200868d44.pdf">ici</a></li>\n	<li>Le bilan du 2ème trimestre de l''année 2020/2021&nbsp;est disponible <a data-link-type="internal" href="/ressources/fichiers/c876273d40c4.pdf">ici</a></li>\n</ul>\n', NULL, '[]', 8153),
(11670, 'BLOCK_QA77C77VN5', 'text', NULL, NULL, '[{"optionType":"colors","priority":80},{"optionType":"effects","priority":90,"_id":"c3581"},{"optionType":"advancedCSS","priority":200,"cssClasses":""}]', '<h2>Des lieux de vie collective facilitant l''épanouissement de chacun:</h2>\n', NULL, '[]', 8154),
(11671, 'BLOCK_GL6W6RS1WL', 'text', NULL, NULL, '[{"optionType":"colors","priority":80},{"optionType":"effects","priority":90,"_id":"c3587"},{"optionType":"advancedCSS","priority":200,"cssClasses":""}]', '<p>- un "espace-cantine" aéré</p>\n\n<p>- des cours de récréation verdoyantes semi-ombragées</p>\n\n<p>- des espaces de jeux multiples: le préau et les tables de tennis de table, le terrain "multisports" et les tricycles et draisiennes...</p>\n', NULL, '[]', 8154),
