(11677, 'BLOCK_OKH03MFGSL', 'button', NULL, NULL, '[{"optionType":"ButtonOption","priority":70,"text":"Contactez-nous","link":{"target":"_self","name":"Contactez-nous","href":4,"anchor":"","type":0,"lang":"en_FR","showTitle":true,"showDesc":true}},{"optionType":"ButtonStyleOption","priority":80,"size":"medium","buttonAlignment":"align-center","textAlignment":"text-center"},{"optionType":"advancedCSS","priority":200,"cssClasses":""}]', NULL, NULL, '[]', 8158),
(11895, 'BLOCK_LR2ZNBIGL0', 'text', NULL, NULL, '[{"optionType":"colors","priority":80},{"optionType":"effects","priority":90,"_id":"c6616"},{"optionType":"advancedCSS","priority":200,"cssClasses":""}]', '<h2>Procedure</h2>\n', NULL, '[]', 8325),
(11896, 'BLOCK_CG1MSTRT2L', 'text', NULL, NULL, '[{"optionType":"colors","priority":80},{"optionType":"effects","priority":90,"_id":"c6622"},{"optionType":"advancedCSS","priority":200,"cssClasses":""}]', '<p style="text-align:justify">To re-enroll your child, please:</p>\n\n<p><strong>1.&nbsp; Complete both documents online by 1&lt;sup&gt;st&lt;/sup&gt; May 2022 by following the links below:</strong></p>\n\n<p><strong>The re-enrollment form :</strong></p>\n\n<p>&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <a href="https://edocsign.orion.mu/form?reference=be7bdeb8-7147-419e-910f-6a9e0361d515" target="_blank">https://edocsign.orion.mu/form?reference=be7bdeb8-7147-419e-910f-6a9e0361d515</a></p>\n\n<p>The bank reference to be specified in the above online document corresponds to the payment of the re-enrolment fees as follows: amount of the deposit fund (5000 Rs), of the participation to the additional fees (3100 Rs) and of the APEP membership fee (300 to 500 Rs depending on the number of children).</p>\n\n<p><strong>The of recognition of obligation to take care of the children at the end of the class form:&nbsp;</strong></p>\n\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <a href="https://edocsign.orion.mu/form?reference=491912a0-0dd9-49de-a202-c4f2b5657734" target="_blank">https://edocsign.orion.mu/form?reference=491912a0-0dd9-49de-a202-c4f2b5657734</a></p>\n\n<p><strong>2. Deposit the signed forms with the receptionist between May 2 and May 31, 2022, along with the completed and signed monthly direct debit form.</strong><strong><u> </u></strong></p>\n\n<p style="text-align:justify">&nbsp;</p>\n\n<p style="text-align:justify"><strong>Internal Rules:</strong></p>\n\n<p style="text-align:justify">We remind you that <u>enrollment in the school implies acceptance of the internal regulations which we invite you to review.</u></p>\n\n<p style="text-align:justify"><strong>You will find all the documents on this site, by clicking on the tab: Registration / Documents.</strong></p>\n\n<p>&nbsp;</p>\n\n<p style="text-align:justify">&nbsp;</p>\n', NULL, '[]', 8325),
(11897, 'BLOCK_R3IS7BZZJQ', 'text', NULL, NULL, '[{"optionType":"colors","priority":80},{"optionType":"effects","priority":90,"_id":"c6637"},{"optionType":"advancedCSS","priority":200,"cssClasses":"emphasis"}]', '<p style="text-align:center">If you have any questions, please do not hesitate to contact us.</p>\n', NULL, '[]', 8326),
(11898, 'BLOCK_REZON6I1S9', 'button', NULL, NULL, '[{"optionType":"ButtonOption","priority":70,"text":"CONTACT US","link":{"target":"_self","name":"Contact us","href":10,"anchor":"","type":0,"lang":"en_FR","showTitle":true,"showDesc":true}},{"optionType":"ButtonStyleOption","priority":80,"size":"medium","buttonAlignment":"align-center","textAlignment":"text-center"},{"optionType":"advancedCSS","priority":200,"cssClasses":""}]', NULL, NULL, '[]', 8326),
